window.onload = function(){


$(function() {
    $('.list-group.checked-list-box .list-group-item').each(function() {
        var $widget = $(this),
            $checkbox = $('<input type="checkbox" class="hidden" />'),
            color = ($widget.data('color') ? $widget.data('color') : "primary"),
            style = ($widget.data('style') == "button" ? "btn-" : "list-group-item-"),
            settings = {
                on: {
                    icon: 'glyphicon glyphicon-check'
                },
                off: {
                    icon: 'glyphicon glyphicon-unchecked'
                }
            };
        $widget.css('cursor', 'pointer')
        $widget.prepend($checkbox);
        $widget.on('click', function() {
            $checkbox.prop('checked', !$checkbox.is(':checked'));
            $checkbox.triggerHandler('change');
            updateDisplay()
        });
        $checkbox.on('change', function() {
            updateDisplay()
        });

        function updateDisplay() {
            var isChecked = $checkbox.is(':checked');
            $widget.data('state', (isChecked) ? "on" : "off");
            $widget.find('.state-icon').removeClass().addClass('state-icon ' + settings[$widget.data('state')].icon);
            if (isChecked) {
                $widget.addClass(style + color + ' active')
            } else {
                $widget.removeClass(style + color + ' active')
            }
            $('#nav1').addClass("nav-underline");

        }
        document.getElementById('reset').onclick = function() {
            $('input:checkbox').removeAttr('checked');
            $('li').removeClass(style + color + ' active');
            $('#nav1').addClass("nav-underline");

        };

        function init() {
            if ($widget.data('checked') == !0) {
                $checkbox.prop('checked', !$checkbox.is(':checked'))
            }
            updateDisplay();
            if ($widget.find('.state-icon').length == 0) {
                $widget.prepend('<span class="state-icon ' + settings[$widget.data('state')].icon + '"></span>')
            }
        }
        init()
    });
    $('#get-checked-data').on('click', function(event) {
        event.preventDefault();
        var checkedItems = {},
            counter = 0;
        $("#check-list-box li.active").each(function(idx, li) {
            checkedItems[counter] = $(li).text();
            counter++
        });
        $('#display-json').html(JSON.stringify(checkedItems, null, '\t'))
    })
})

$("#nav1").click(function(){
    $("#gym").hide();
    $("#grocery").show();
    $('#nav1').addClass("nav-underline");
})

$("#nav2").click(function(){
    $("#grocery").hide();
    $("#gym").show();
    $('#nav2').addClass("nav-underline");
})

};
